import React, { createContext, useContext, useState, useEffect } from 'react';

// Get API base URL from environment or use default
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api';

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isEmailVerified, setIsEmailVerified] = useState(false);

  // User tracking data
  const [userSession, setUserSession] = useState({
    ipAddress: null,
    browserInfo: null,
    timestamp: null,
    location: null
  });

  // Get user's device and location info
  const getUserInfo = async () => {
    try {
      // Get browser info only (no external API calls)
      const browserInfo = {
        userAgent: navigator.userAgent,
        platform: navigator.platform,
        language: navigator.language,
        cookieEnabled: navigator.cookieEnabled,
        onLine: navigator.onLine,
        screenResolution: `${window.screen.width}x${window.screen.height}`,
        timezone: Intl.DateTimeFormat().resolvedOptions().timeZone
      };

      return {
        ipAddress: 'Not collected',
        browserInfo,
        timestamp: new Date().toISOString(),
        location: {
          country: 'Not collected',
          region: 'Not collected',
          city: 'Not collected',
          timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
          isp: 'Not collected'
        }
      };
    } catch (error) {
      console.error('Error getting user info:', error);
      return {
        ipAddress: 'Unknown',
        browserInfo: {
          userAgent: navigator.userAgent,
          platform: navigator.platform,
          language: navigator.language
        },
        timestamp: new Date().toISOString(),
        location: { country: 'Unknown' }
      };
    }
  };

  // Signup function
  const signup = async (userData, captchaToken) => {
    try {
      setLoading(true);

      // Get user tracking info
      const trackingInfo = await getUserInfo();

      const signupData = {
        ...userData,
        captchaToken,
        trackingInfo,
        emailVerified: false,
        status: 'pending_verification'
      };

      // Send to backend API
      const response = await fetch(`${API_BASE_URL}/auth/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(signupData)
      });

      const result = await response.json();

      if (response.ok) {
        setUserSession(trackingInfo);
        return { success: true, message: 'Verification email sent! Please check your inbox.' };
      } else {
        throw new Error(result.message || 'Signup failed');
      }
    } catch (error) {
      console.error('Signup error:', error);
      return { success: false, message: error.message };
    } finally {
      setLoading(false);
    }
  };

  // Email verification function
  const verifyEmail = async (verificationToken) => {
    try {
      setLoading(true);

      const response = await fetch(`${API_BASE_URL}/auth/verify-email`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ token: verificationToken })
      });

      const result = await response.json();

      if (response.ok) {
        setIsEmailVerified(true);
        setCurrentUser(result.user);
        return { success: true, message: 'Email verified successfully!' };
      } else {
        throw new Error(result.message || 'Verification failed');
      }
    } catch (error) {
      console.error('Verification error:', error);
      return { success: false, message: error.message };
    } finally {
      setLoading(false);
    }
  };

  // Login function
  const login = async (email, password, captchaToken, source = 'unknown') => {
    try {
      console.log('ðŸš€ Login attempt started from:', source, { email, API_BASE_URL });
      console.log('ðŸ” Parameters received:', { email, password: '[HIDDEN]', captchaToken, source });
      console.log('ðŸ” CALL STACK:', new Error().stack);
      setLoading(true);

      const trackingInfo = await getUserInfo();
      console.log('ðŸ“Š Tracking info:', trackingInfo);

      const loginUrl = `${API_BASE_URL}/auth/login`;
      console.log('ðŸŒ Making request to:', loginUrl);

      const requestBody = { 
        email, 
        password, 
        captchaToken,
        trackingInfo 
      };
      console.log('ðŸ“ Request body:', { ...requestBody, password: '[HIDDEN]' });

      const response = await fetch(loginUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody)
      });

      console.log('ðŸ“¡ Response status:', response.status);
      console.log('ðŸ“¡ Response headers:', Object.fromEntries(response.headers.entries()));

      const result = await response.json();
      console.log('ðŸ“¦ Response data:', { ...result, token: result.token ? '[TOKEN]' : 'NO_TOKEN' });

      if (response.ok) {
        console.log('âœ… Login successful, setting user:', result.user);
        setCurrentUser(result.user);
        setIsEmailVerified(result.user.emailVerified);
        setUserSession(trackingInfo);
        localStorage.setItem('token', result.token);
        console.log('âœ… Token saved to localStorage:', result.token ? 'YES' : 'NO');
        console.log('âœ… Current user set:', !!result.user);
        return { success: true, user: result.user };
      } else {
        // Check if this is an email verification error
        if (response.status === 403 && result.error === 'Email not verified') {
          return { 
            success: false, 
            message: 'Email not verified. Please verify your email before logging in.',
            needsVerification: true, 
            email: result.email 
          };
        } else {
          throw new Error(result.message || result.error || 'Login failed');
        }
      }
    } catch (error) {
      console.error('âŒ Login error:', error);
      return { success: false, message: error.message };
    } finally {
      setLoading(false);
    }
  };

  // Logout function
  const logout = () => {
    setCurrentUser(null);
    setIsEmailVerified(false);
    setUserSession({});
    localStorage.removeItem('token');
  };

  // Resend verification email
  const resendVerificationEmail = async (email) => {
    try {
      const response = await fetch(`${API_BASE_URL}/auth/resend-verification`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email })
      });

      const result = await response.json();
      return result;
    } catch (error) {
      console.error('Resend verification error:', error);
      return { success: false, message: error.message };
    }
  };

  useEffect(() => {
    // Check if user is logged in on mount
    const token = localStorage.getItem('token');
    if (token) {
      // Verify token with backend
      fetch(`${API_BASE_URL}/auth/verify-token`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ token })
      })
      .then(response => response.json())
      .then(result => {
        if (result.success) {
          setCurrentUser(result.user);
          setIsEmailVerified(result.user.emailVerified);
        } else {
          localStorage.removeItem('token');
        }
      })
      .catch(error => {
        console.error('Token verification failed:', error);
        localStorage.removeItem('token');
      })
      .finally(() => {
        setLoading(false);
      });
    } else {
      setLoading(false);
    }
  }, []);

  const value = {
    currentUser,
    isEmailVerified,
    userSession,
    signup,
    login,
    logout,
    verifyEmail,
    resendVerificationEmail,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
